$(function(){$("#fullscreen").click(function(){switch(!0){case document.fullScreenEnabled:document.fullscreenElement?document.exitFullscreen():document.documentElement.requestFullscreen();break;case document.webkitFullscreenEnabled:document.webkitFullscreenElement?document.webkitExitFullscreen():document.documentElement.webkitRequestFullscreen();break;case document.mozFullScreenEnabled:document.mozFullScreenElement?document.mozCancelFullScreen():document.documentElement.mozRequestFullScreen();break;case document.msFullscreenEnabled:document.msFullscreenElement?document.msExitFullscreen():document.documentElement.msRequestFullscreen()}})});